import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Student } from '../student.model';
import { StudentsService } from 'src/app/services/students.service';
import { ActivatedRoute, Data, Router } from '@angular/router';
import Swal from 'sweetalert2';
import { async } from '@angular/core/testing';
import { NgbModal, ModalDismissReasons } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-student-detail',
  templateUrl: './student-detail.component.html',
  styleUrls: ['./student-detail.component.css']
})
export class StudentDetailComponent implements OnInit {
  student: Student = {} as Student;
  closeResult = '';

  constructor(private studentsService: StudentsService, private route: ActivatedRoute, private router: Router, private modalService: NgbModal) {}

  ngOnInit(): void {
    this.route.data.subscribe(
      (data: Data) => {
        this.student = data['student']
      }
    );

    this.studentsService.studentDeleted.subscribe(
      (executed) => {
        if(executed) {
          Swal.fire(
            'Deleted!',
            'Your file has been deleted.',
            'success'
          )
        } else {
          Swal.fire(
            'Error!',
            'Could not delete student!',
            'error'
          )
        }
      }
    )
  }

  async onDelete(studentId: number) {
    Swal.fire({
      title: 'Are you sure?',
      text: "You will permanently delete this student!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Yes, delete it!'
    }).then( async (result) => {
      if (result.isConfirmed) {
        this.studentsService.deleteStudent(studentId);
        this.router.navigate(['../'], {relativeTo: this.route});
      }
    })
  }

  open(content: any) {
    this.modalService.open(content, {ariaLabelledBy: 'modal-basic-title'}).result.then((result) => {
      this.closeResult = `Closed with: ${result}`;
    }, (reason) => {
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });
  }

  private getDismissReason(reason: any): string {
    if (reason === ModalDismissReasons.ESC) {
      return 'by pressing ESC';
    } else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
      return 'by clicking on a backdrop';
    } else {
      return `with: ${reason}`;
    }
  }
}
